<script>
$(function() {
	$('#name').keyup(function(e){
		if ( e.which == 13 ) {
			e.preventDefault();
		}
		var name = $(this).val();
		$('#yourname').html( name )
	});
	$('#job').keyup(function(e){
		if ( e.which == 13 ) {
			e.preventDefault();
		}
		var job = $(this).val();
		$('#yourjob').html( job );
	});
	$('#email').keyup(function(e){
		if ( e.which == 13 ) {
			e.preventDefault();
		}
		var email = $(this).val();
		$('#youremail').html( '<a href="mailto:' + email + '" style="color: #0d69c9;">' + email + '</a>' );
	});
	$('#ddi').keyup(function(e){
		if ( e.which == 13 ) {
			e.preventDefault();
		}
		var ddi = $(this).val();
		$('#yourddi').html( '<a href="tel:' + ddi + '" style="color: #0d69c9;">' + ddi + '</a>' );
	});
	$('#mobile').keyup(function(e){
		if ( e.which == 13 ) {
			e.preventDefault();
		}
		var mobile = $(this).val();
		$('#yourmobile').html( '<a href="tel:' + mobile + '" style="color: #0d69c9;">' + mobile + '</a>' );
	});
});
</script>
